import {React , useState} from 'react';
import './weather.css';

const Weather= ()=> {

  const [weatherInfo , setWeatherInfo] = useState({
    temperature : '' , city : '' , country : '', humidity : '', description : '', error: ''
  }) 
  const apiUrl = "160cf158de4d9bbe0be7da4be3bf54f0";

  const getWeather = async (e)=>{
    e.preventDefault();
    const country = e.target.elements.country.value;
    const city = e.target.elements.city.value;
    const url = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}%2C${country}&appid=e36ed364400282e43250b6c4c0274d44`);
    const data = await url.json();
    console.log(data);
    setWeatherInfo({temperature : data , city : '' , country : data.sys.country , humidity : '', description : data.weather.de, error: ''  })
  }   
  return (
    <div className='container'>
        <form onSubmit={getWeather}>
            <input type='text' name='country' placeholder='Country' />
            <input type='text' name='city' placeholder='City' />
            <button> Get Weather</button>
        </form>
        <div className='info'>
            <div className='location'>
                <span> Location : </span>
                <span></span>

            </div>
            <div className='temp'>
                <span> Temperature : </span>
                <span></span>
            </div>
            <div className='humid'>
                <span> Humidity : </span>
                <span> </span>
            </div>
            <div className='condi'>
                <span> Condition :</span>
                <span></span>


            </div>


        </div>
    </div>
  )
}

export default Weather
